import { useEffect, useRef } from 'react';
import './index.css';

const MainInput = ({inputValue, setInputValue, onHandleSubmit, movieData}) => {
  const inputRef = useRef(null);

  useEffect(() => {
    inputRef.current.focus()
  }, []);

  const onHandleInput = (e) => setInputValue(e.target.value)

  return (
    <form className="MainInput" onSubmit={onHandleSubmit}>
      <select ref={inputRef} value={e.target.option.text} onChange={onHandleInput} type="text" >
      <option value="1271">300</option>
      <option value="597">Titanic</option>
      <option value="27205">Inception</option>
      </select>
      <button type="submit">Search</button>
    </form>
  )
}

export default MainInput;
